	/**
	 * @param vDayStr
	 * @return
	 */
	private Date getDateParsing(String vDayStr) {
		DateFormat format = new SimpleDateFormat("MMddyyyy");
		Date vDay = null;
		try {
			vDay = format.parse(vDayStr);
		} catch (ParseException e1) {
			Log.e("Date Parse Exception", "Date Parse Exception");
		}
		return vDay;
	}

