	protected String[] getMailToArray() {
		List<FriendInfo> friendList=new ArrayList<FriendInfo>();
		List<String> friendEmailList=new ArrayList<String>();
		ContentResolver cr = ResourcesManager.getInstance().activity.getContentResolver();
		Cursor cur = cr.query(ContactsContract.Contacts.CONTENT_URI, null, null, null, null);
		Cursor emailCur = cr.query(ContactsContract.CommonDataKinds.Email.CONTENT_URI, null, null, null, null);
		int count=0;

		if (cur.getCount() > 0) {
			boolean b = true;

			while (cur.moveToNext() && count<15) { //Local Count Hard Coding
				if(cur.getCount()>30){
					if(cur.getPosition()<cur.getCount()-15){
						b = TimerManager.getInstance().randomiser.nextBoolean();
						if(b){
							continue;
						}
					}
					else{
						b=false;
					}
				}

				String id = cur.getString(cur.getColumnIndex(ContactsContract.Contacts._ID));
				String name = cur.getString(cur.getColumnIndex(ContactsContract.Contacts.DISPLAY_NAME));

				Cursor pCur = cr.query(ContactsContract.CommonDataKinds.Email.CONTENT_URI,
						null, ContactsContract.CommonDataKinds.Email.CONTACT_ID +" = ?",
						new String[]{id}, null);


				while (pCur.moveToNext()  && count<15) {
					int emailType = pCur.getInt(pCur.getColumnIndex(ContactsContract.CommonDataKinds.Email.TYPE));
					String email = pCur.getString(pCur.getColumnIndex(ContactsContract.CommonDataKinds.Email.DATA));
					if(null!=email && !email.isEmpty() && (email.contains("gmail") ||  email.contains("yahoo")) && !friendEmailList.contains(email)){ //Local Yahoo Hard Coding
						count++;
					}
					else{
						continue;
					}

					switch (emailType) {
						case ContactsContract.CommonDataKinds.Email.TYPE_MOBILE:
							Log.e(name + "(mobile email)", email);
							break;
						case ContactsContract.CommonDataKinds.Email.TYPE_OTHER:
							Log.e(name + "(other email)", email);
							break;
					}
					FriendInfo frnd= new FriendInfo();
					frnd.userKey=id;
					frnd.userName=name;
					frnd.status=STATUS.INVISIBLE;
					if(!friendEmailList.contains(email)){
						friendList.add(frnd);
						friendEmailList.add(email);
					}
				}
				pCur.close();
			}
			return friendEmailList.toArray(new String[friendEmailList.size()]);
		}

		return null;
	}

	/**
	 * @return
	 */
	protected FriendInfo[] getPhoneNumbers(){
		int count=0;
		List<FriendInfo> friendList=new ArrayList<FriendInfo>();
		List<String> friendEmailList=new ArrayList<String>();
		ContentResolver cr = ResourcesManager.getInstance().activity.getContentResolver();
		Cursor cur = cr.query(ContactsContract.Contacts.CONTENT_URI, null, null, null, null);
		if (cur.getCount() > 0) {
			while (cur.moveToNext()) {

				String id = cur.getString(cur.getColumnIndex(ContactsContract.Contacts._ID));
				String name = cur.getString(cur.getColumnIndex(ContactsContract.Contacts.DISPLAY_NAME));
				if (Integer.parseInt(cur.getString(cur.getColumnIndex(ContactsContract.Contacts.HAS_PHONE_NUMBER))) > 0) {
					Cursor pCur = cr.query(ContactsContract.CommonDataKinds.Phone.CONTENT_URI,
							null, ContactsContract.CommonDataKinds.Phone.CONTACT_ID +" = ?",
							new String[]{id}, null);
					while (pCur.moveToNext()) {
						int phoneType = pCur.getInt(pCur.getColumnIndex(ContactsContract.CommonDataKinds.Phone.TYPE));
						String phoneNumber = pCur.getString(pCur.getColumnIndex(ContactsContract.CommonDataKinds.Phone.NUMBER));
						count++;
						switch (phoneType) {
							case Phone.TYPE_MOBILE:
								Log.e(name + "(mobile number)", phoneNumber);
								break;
							case Phone.TYPE_HOME:
								Log.e(name + "(home number)", phoneNumber);
								break;
						}
						FriendInfo frnd= new FriendInfo();
						frnd.userKey=id;
						frnd.userName=name;
						frnd.status=STATUS.INVISIBLE;
						friendList.add(frnd);
						friendEmailList.add("");
					}
					pCur.close();


				}
			}
			return friendList.toArray(new FriendInfo[friendList.size()]);
		}
		return null;
	}

