//GET Image using id for email
class ImageGetter implements Html.ImageGetter {

	public Drawable getDrawable(String source) {
		int id;

		id = ResourcesManager.getInstance().activity.getResources().getIdentifier(source, "raw", ResourcesManager.getInstance().activity.getPackageName());

		if (id == 0) {
			// the drawable resource wasn't found in our package, maybe it is a stock android drawable?
			id = ResourcesManager.getInstance().activity.getResources().getIdentifier(source, "drawable", "android");
		}

		if (id == 0) {
			// prevent a crash if the resource still can't be found
			// Save this bitmap to a file.
			BitmapDrawable bitmapDrawable = (BitmapDrawable)ResourcesManager.getInstance().activity.getResources().getDrawable(R.drawable.qrcodcompressed);
			return bitmapDrawable;
		}
		else {
			Drawable d = ResourcesManager.getInstance().activity.getResources().getDrawable(id);
			d.setBounds(0,0,d.getIntrinsicWidth(),d.getIntrinsicHeight());
			return d;
		}
	}

}